module I3ConfigGen
  class Methods
    def self.modifiers
      %w[
Shift_R
Shift_L
Control_R
Control_L
Alt_R
Alt_L
      ]
    end

    def self.keys
      `xmodmap -pk`.lines
        .map{ |l| Array(l.match(/\((.*?)\)/))&.[](1) }
        .compact.uniq
        .reject{|m| modifiers.include?(m)}
    end

    def self.special_mode_exit
      "exec --no-startup-id special_mode_exit"
    end

    def self.base
      <<~BASE
      # This file has been auto-generated by an i3 wrapper
      # It will be overwritten anytime i3 is restarted.
      #
      # Please see http://i3wm.org/docs/userguide.html for a complete reference!

      set $mod Mod4
      set $homedir "/home/kwstannard/"

      hide_edge_borders both

      focus_follows_mouse no
      # max terminal screen space
      for_window [title=".*Konsole$"] border none
      for_window [title="Terminal"] border none
      for_window [class="Firefox"] border none
      for_window [class="Slack"] border none

      # Font for window titles. Will also be used by the bar unless a different font
      # is used in the bar {} block below.
      # This font is widely installed, provides lots of unicode glyphs, right-to-left
      # text rendering and scalability on retina/hidpi displays (thanks to pango).
      font pango:DejaVu Sans Mono 12
      # Before i3 v4.8, we used to recommend this one as the default:
      # font -misc-fixed-medium-r-normal--13-120-75-75-C-70-iso10646-1
      # The font above is very space-efficient, that is, it looks good, sharp and
      # clear in small sizes. However, its unicode glyph coverage is limited, the old
      # X core fonts rendering does not support right-to-left and this being a bitmap
      # font, it doesn’t scale on retina/hidpi displays.

      # Use Mouse+$mod to drag floating windows to their wanted position
      floating_modifier $mod

      #set $current_workspace "
        # $(i3-msg -t get_workspaces |
          #jq -r '.[] | "\(.name) \(.focused)"' |
          #grep true |
          #sed 's/ \w*$//')
      #"
      BASE
    end

    def self.modes
      <<~MODES
      # resize window (you can also use the mouse for that)
      mode "resize" {
      # These bindings trigger as soon as you enter the resize mode

      # Pressing left will shrink the window’s width.
      # Pressing right will grow the window’s width.
      # Pressing up will shrink the window’s height.
      # Pressing down will grow the window’s height.
        bindsym j resize shrink width 10 px or 10 ppt
          bindsym k resize grow height 10 px or 10 ppt
          bindsym l resize shrink height 10 px or 10 ppt
          bindsym semicolon resize grow width 10 px or 10 ppt

      # same bindings, but for the arrow keys
          bindsym Left resize shrink width 10 px or 10 ppt
          bindsym Down resize grow height 10 px or 10 ppt
          bindsym Up resize shrink height 10 px or 10 ppt
          bindsym Right resize grow width 10 px or 10 ppt

      # back to normal: Enter or Escape
          bindsym Shift+Escape mode "default"
          bindsym Return #{special_mode_exit}
          bindsym Escape #{special_mode_exit}
      }

      set $mode_movement Movement
      mode "$mode_movement" {
      # move focused window
        bindsym $mod+h move left
        bindsym $mod+j move down
        bindsym $mod+k move up
        bindsym $mod+l move right

      # alternatively, you can use the cursor keys:
        bindsym $mod+Left move left
        bindsym $mod+Down move down
        bindsym $mod+Up move up
        bindsym $mod+Right move right

          # back to normal: Enter or Escape
          bindsym Shift+Escape mode "default"
          bindsym Return #{special_mode_exit}
          bindsym Escape #{special_mode_exit}
      }

      set $mode_system System (l) lock, (e) logout, (s) suspend, (h) hibernate, (r) reboot, (Shift+s) shutdown
      mode "$mode_system" {
        bindsym l exec --no-startup-id custom_locker, #{special_mode_exit}
          bindsym e exec --no-startup-id i3msg exit, #{special_mode_exit}
          bindsym s exec --no-startup-id custom_locker && systemctl suspend, #{special_mode_exit}
          bindsym h exec --no-startup-id custom_locker && systemctl hibernate, #{special_mode_exit}
          bindsym r exec --no-startup-id systemctl reboot, #{special_mode_exit}
          bindsym Shift+s exec --no-startup-id systemctl poweroff, #{special_mode_exit}

          # back to normal: Enter or Escape
          bindsym Shift+Escape mode "default"
          bindsym Return #{special_mode_exit}
          bindsym Escape #{special_mode_exit}
      }
      MODES
    end

    def self.function_key_binds
      <<~BINDS
      # use xev to determine correct keys if these don't work
      bindsym XF86AudioRaiseVolume exec --no-startup-id amixer -D pulse sset Master 5%+ unmute
      bindsym XF86AudioLowerVolume exec --no-startup-id amixer -D pulse sset Master 5%- unmute
      bindsym XF86AudioMute exec --no-startup-id amixer -D pulse sset Master toggle

      # Sreen brightness controls
      # i3 doesn't seem to let you modify the audio buttons
      #"XF86AudioRaiseVolume" => "exec --no-startup-id xbacklight -inc 10",
      #"XF86AudioLoweraiseVolume" => "exec --no-startup-id xbacklight -dec 10",
      bindsym XF86MonBrightnessUp exec xbacklight -inc 20 # increase screen brightness
      bindsym XF86MonBrightnessDown exec xbacklight -dec 20 # decrease screen brightness

      # Touchpad controls
      bindsym XF86TouchpadToggle exec /some/path/toggletouchpad.sh # toggle touchpad

      # Media player controls
      bindsym XF86AudioPlay exec playerctl play
      bindsym XF86AudioPause exec playerctl pause
      bindsym XF86AudioNext exec playerctl next
      bindsym XF86AudioPrev exec playerctl previous
      BINDS
    end

    def self.other
      <<~OTHER
      # Start i3bar to display a workspace bar (plus the system information i3status
      # finds out, if available)
      bar {
        workspace_buttons no
              status_command i3status
              bindsym button1 nop
              bindsym button2 nop
              bindsym button3 nop
              bindsym button4 nop
              bindsym button5 nop
              bindsym button6 nop
              bindsym button7 nop
              bindsym button8 nop
              bindsym button9 nop
      }
      exec --no-startup-id nm-applet

      assign [class="Firefox"] 14: Firefox
      assign [class="Slack"] 13: Slack
      assign [class="Pidgin"] 12: Pidgin
      assign [class="Keepassx"] 11: Keepass
      assign [title="work_console"] 100: WorkConsole

      # startup apps
      exec --no-startup-id keepassx #{ENV["KEEPASSX_DB"]}
      exec --no-startup-id firefox
      exec --no-startup-id /usr/bin/slack
      exec --no-startup-id pidgin

      exec --no-startup-id konsole --profile work_console
      exec --no-startup-id cron_notify_setup

      #screensaver + lock on screen saver
      exec_always --no-startup-id xautolock -time 15 -locker custom_lock

      OTHER
      #assign [class="Korganizer"] 101: Calendar
      #exec --no-startup-id korganizer
    end


    def self.common_binds
      {
        # start a terminal
        "Return" => "exec konsole",
        "Shift+Return" => "split h; exec konsole; split v",

        # kill focused window
        "Shift+q" => "kill",

        # start dmenu (a program launcher)
        # "d" => "exec dmenu_run",
        "Shift+a" => "split toggle; exec dmenu_run; split toggle",
        # There also is the (new) i3-dmenu-desktop which only displays applications
        # shipping a .desktop file. It is a wrapper around dmenu, so you need that
        # installed.
        "a" => "exec --no-startup-id i3-dmenu-desktop",

        # change focus
        "h" => "focus left",
        "j" => "focus down",
        "k" => "focus up",
        "l" => "focus right",

        # alternatively, you can use the cursor keys:
        "Left" => "focus left",
        "Down" => "focus down",
        "Up" => "focus up",
        "Right" => "focus right",

        # next/prev workspace
        "Shift+Left" => "workspace prev",
        "Shift+Right" => "workspace next",


        "b" => "split t",

        # enter fullscreen mode for the focused container
        "Shift+f" => "fullscreen",

        # change container layout (stacked, tabbed, toggle split)
        #"s" => "layout stacking",
        #"w" => "layout tabbed",
        #"e" => "layout toggle split",

        # toggle tiling / floating
        "Shift+space" => "floating toggle",

        # change focus between tiling / floating windows
        "space" => "focus mode_toggle",

        # focus the parent container
        #"a" => "focus parent",

        # focus the child container
        #"d" => "focus child",

        "backslash" => Goto("100: WorkConsole"),
        "p" => Goto("14: Firefox"),
        "o" => Goto("13: Slack"),
        "i" => Goto("12: Pidgin"),
        "Shift+u" => Goto("11: Keepass"),

        "u" => <<~KPDM,
        exec --no-startup-id i3exec keepass-dmenu \
        --database $homedir/Dropbox/puppies.kdbx \
        --label password \
        --cache-password $((86400 - `date +%s` % 86400 + 7*3600 ))
        KPDM

        "y" => "workspace 101: Calendar",

        # reload the configuration file
        "Shift+c" => "reload",
        # restart i3 inplace (preserves your layout/session, can be used to upgrade i3)
        "Shift+r" => "restart",

        "r" => "mode \"resize\"",
        "m" => "mode \"$mode_movement\"",
        "End" => "mode \"$mode_system\"",

        "Escape" => "exec custom_locker",

        "Prior" => "exec --no-startup-id amixer -D pulse sset Master 5%+ unmute",
        "Next" => "exec --no-startup-id amixer -D pulse sset Master 5%- unmute",
        "Shift+Prior" => "exec --no-startup-id xbacklight -inc 10",
        "Shift+Next" => "exec --no-startup-id xbacklight -dec 10",
      }
    end

  end

  class Workspace < Struct.new(:key, :id)
    def add_bindings_to(binding_set)
      binding_set.add(*switch_to)
      binding_set.add(*swap_with)
      binding_set.add(*move_to)
    end

    def switch_to
      [key, Goto(id)]
    end

    def swap_with
      ["Shift+#{key}", "exec --no-startup-id swap_workspace #{id}"]
    end

    def move_to
      ["Control+#{key}", "move window to workspace #{id}"]
    end
  end

  class ProjectMode < Methods
    @@count = 0
    @@projects = []

    def self.new(*args)
      @@count += 1
      @@projects << super(*args)
    end

    def self.text
      @@projects.map{|p| p.text}.join("\n\n")
    end

    attr_accessor :name
    def initialize(name, keys)
      self.name = name
      @workspace_group = @@count
      @keys = keys
      @var = "$mode_" + name.downcase.tr(" ", "_")

      bindings = BindingSet.new
      (1..5).each do |i|
        Workspace.new(i, "#{@workspace_group}#{i + 4}").add_bindings_to(bindings)
      end

      bindings.add("d", "workspace #{@workspace_group}5")
      bindings.add("f", "workspace #{@workspace_group}4")
      bindings.add("s", "workspace #{@workspace_group}2")

      self.class.common_binds.each{|key,action| bindings.add(key, action) }

      @text = <<~MODE
      set #{@var} #{name}
      mode "#{@var}" {
        #{self.class.function_key_binds.lines.join("  ")}
        #{bindings.to_config}
      MODE
    end

    def global_binding
      "bindsym #{@keys} mode \"#{@var}\";"\
        "#{Goto("#{@workspace_group}5")};"\
        "exec --no-startup-id echo #{name} > ~/.i3_project;"\
        "exec --no-startup-id ln -sf ~/.config/i3/#{name}_config.sh ~/.current_config.sh"
    end

    def text
      @text + "\n" +
        "  " + (self.class.global_bindings).lines.join("  ") +
        "\n}\n" +
        global_binding + "\n"
    end

    def self.global_bindings
      @@projects.map(&:global_binding).join("\n")
    end
  end

  class BindingSet < Methods
    attr_accessor :i_keys
    def initialize
      self.i_keys = Hash.new(self.class.keys.product(["nop"]))
    end

    def add(key, action)
      raise "key '#{key}' already bound" if i_keys.has_key?(key)

      i_keys[key] = action
    end

    def to_config
      i_keys.map{|key,action| "bindsym $mod+#{key} #{action}"}.join("\n  ")
    end
  end

  def self.call
    bindings = BindingSet.new
    Methods.common_binds.each{|key,action| bindings.add(key, action) }

    ProjectMode.new("environment", "$mod+minus")

    min = 1; max = 5
    (min..max).each{|i| ProjectMode.new("Project_#{max - i}", "$mod+#{(11 - i) % 10}")}

    Methods.base + Methods.modes + ProjectMode.text + bindings.to_config + Methods.function_key_binds + Methods.other
  end
end

  def Goto(workspace)
    "workspace #{workspace}; exec --no-startup-id echo #{workspace} > ~/.i3_workspace"
  end
